  var cache_nls = OpenAjax.a11y.nls.Cache;
  var i;
  
  var attributes  = [];
  
  for (i = 0; i < this.aria_attributes.length; i++) {
    var aa = this.aria_attributes[i];
    attributes.push(cache_nls.getLabelAndValueNLS(aa.name, aa.value));
  }

