          else {
           rule_result.addResult(TEST_RESULT.HIDDEN, ce, 'ELEMENT_HIDDEN_2', []);                          }
        }

        if (ce.child_cache_elements && ce.child_cache_elements.length) {
          has_submit = has_submit || has_submit_button(ce);
        }

      }

      return has_submit;

