        if (ce.control_type === CONTROL_TYPE.BUTTON_INPUT) {
          if (cs.is_visible_to_at === VISIBILITY.VISIBLE) {
            rule_result.addResult(TEST_RESULT.MANUAL_CHECK, ce, 'ELEMENT_MC_3', ['button']);
            count += 1;
          }
